function courses_table_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('courses_table');
	if(!$arrPerm['insert']) {
		$error_message = $Translation['no insert permission'];
		return false;
	}

	$data = [
		'course_name' => Request::val('course_name', ''),
		'department' => Request::lookup('department', ''),
		'starting_date' => Request::dateComponents('starting_date', '1'),
		'ending_date' => Request::dateComponents('ending_date', '1'),
		'duration' => Request::val('duration', ''),
		'credits' => Request::val('credits', ''),
		'created_at' => parseCode('<%%creationDateTime%%>', true),
		'created_by_username' => parseCode('<%%creatorUsername%%>', true),
	];

	// record owner is current user
	$recordOwner = getLoggedMemberID();

	$recID = tableInsert('courses_table', $data, $recordOwner, $error_message);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID')) && $recID !== false)
		courses_table_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function courses_table_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);
	$currentUsername = getLoggedMemberID();
	$errorMessage = '';

	// launch requests, asynchronously
	curl_batch($requests);
}

