function courses_table_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('courses_table', $selected_id, 'edit')) return false;

	$data = [
		'course_name' => Request::val('course_name', ''),
		'department' => Request::lookup('department', ''),
		'starting_date' => Request::dateComponents('starting_date', ''),
		'ending_date' => Request::dateComponents('ending_date', ''),
		'duration' => Request::val('duration', ''),
		'credits' => Request::val('credits', ''),
		'last_updated_at' => parseCode('<%%editingDateTime%%>', false),
		'last_updated_by_username' => parseCode('<%%editorUsername%%>', false),
	];

	// get existing values
	$old_data = getRecord('courses_table', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: courses_table_before_update
	if(function_exists('courses_table_before_update')) {
		$args = ['old_data' => $old_data];
		if(!courses_table_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'courses_table',
		backtick_keys_once($set),
		['`id`' => $selected_id],
		$error_message
	)) {
		echo $error_message;
		echo '<a href="courses_table_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	update_calc_fields('courses_table', $data['selectedID'], calculated_fields()['courses_table']);

	// hook: courses_table_after_update
	if(function_exists('courses_table_after_update')) {
		if($row = getRecord('courses_table', $data['selectedID'])) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!courses_table_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('courses_table', $selected_id);
}

