	// set records to read only if user can't insert new records and can't edit current record
	if(!$fieldsAreEditable) {
		$jsReadOnly = '';
		$jsReadOnly .= "\t\$j('#course_name').replaceWith('<div class=\"form-control-static\" id=\"course_name\">' + (\$j('#course_name').val() || '') + '</div>');\n";
		$jsReadOnly .= "\t\$j('#department').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\t\$j('#department_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\t\$j('#starting_date').prop('readonly', true);\n";
		$jsReadOnly .= "\t\$j('#starting_dateDay, #starting_dateMonth, #starting_dateYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\t\$j('#ending_date').prop('readonly', true);\n";
		$jsReadOnly .= "\t\$j('#ending_dateDay, #ending_dateMonth, #ending_dateYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\t\$j('#duration').replaceWith('<div class=\"form-control-static\" id=\"duration\">' + (\$j('#duration').val() || '') + '</div>');\n";
		$jsReadOnly .= "\t\$j('#credits').replaceWith('<div class=\"form-control-static\" id=\"credits\">' + (\$j('#credits').val() || '') + '</div>');\n";
		$jsReadOnly .= "\t\$j('.select2-container').hide();\n";

		$noUploads = true;
	} else {
		// temporarily disable form change handler till time and datetime pickers are enabled
		$jsEditable = "\t\$j('form').eq(0).data('already_changed', true);";
		$jsEditable .= "\t\$j('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(department)%%>', $combo_department->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(department)%%>', $combo_department->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(department)%%>', urlencode($combo_department->MatchText), $templateCode);
	$templateCode = str_replace(
		'<%%COMBO(starting_date)%%>',
		(!$fieldsAreEditable ?
			'<div class="form-control-static">' . $combo_starting_date->GetHTML(true) . '</div>' :
			$combo_starting_date->GetHTML()
		), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(starting_date)%%>', $combo_starting_date->GetHTML(true), $templateCode);
	$templateCode = str_replace(
		'<%%COMBO(ending_date)%%>',
		(!$fieldsAreEditable ?
			'<div class="form-control-static">' . $combo_ending_date->GetHTML(true) . '</div>' :
			$combo_ending_date->GetHTML()
		), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(ending_date)%%>', $combo_ending_date->GetHTML(true), $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['department' => ['departments_table', 'Department'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if(($pt_perm['view'] && isDetailViewEnabled($ptfc[0])) || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(course_name)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(department)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(starting_date)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(ending_date)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(duration)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(credits)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(created_by)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(created_at)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(last_updated_by)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(last_updated_at)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(created_by_username)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(last_updated_by_username)%%>', '', $templateCode);

	// process values
	if($hasSelectedId) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(course_name)%%>', safe_html($urow['course_name']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(course_name)%%>', html_attr($row['course_name']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(course_name)%%>', urlencode($urow['course_name']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(department)%%>', safe_html($urow['department']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(department)%%>', html_attr($row['department']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(department)%%>', urlencode($urow['department']), $templateCode);
		$templateCode = str_replace('<%%VALUE(starting_date)%%>', app_datetime($row['starting_date']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(starting_date)%%>', urlencode(app_datetime($urow['starting_date'])), $templateCode);
		$templateCode = str_replace('<%%VALUE(ending_date)%%>', app_datetime($row['ending_date']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ending_date)%%>', urlencode(app_datetime($urow['ending_date'])), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(duration)%%>', safe_html($urow['duration']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(duration)%%>', html_attr($row['duration']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(duration)%%>', urlencode($urow['duration']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(credits)%%>', safe_html($urow['credits']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(credits)%%>', html_attr($row['credits']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(credits)%%>', urlencode($urow['credits']), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_by)%%>', safe_html($urow['created_by']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_by)%%>', urlencode($urow['created_by']), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_at)%%>', safe_html($urow['created_at']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_at)%%>', urlencode($urow['created_at']), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_updated_by)%%>', safe_html($urow['last_updated_by']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_updated_by)%%>', urlencode($urow['last_updated_by']), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_updated_at)%%>', safe_html($urow['last_updated_at']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_updated_at)%%>', urlencode($urow['last_updated_at']), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_by_username)%%>', safe_html($urow['created_by_username']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_by_username)%%>', urlencode($urow['created_by_username']), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_updated_by_username)%%>', safe_html($urow['last_updated_by_username']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_updated_by_username)%%>', urlencode($urow['last_updated_by_username']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(course_name)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(course_name)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(department)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(department)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(starting_date)%%>', '1', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(starting_date)%%>', urlencode('1'), $templateCode);
		$templateCode = str_replace('<%%VALUE(ending_date)%%>', '1', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(ending_date)%%>', urlencode('1'), $templateCode);
		$templateCode = str_replace('<%%VALUE(duration)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(duration)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(credits)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(credits)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_by)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_by)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_at)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_at)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_updated_by)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_updated_by)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_updated_at)%%>', '<%%editingDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_updated_at)%%>', urlencode('<%%editingDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_by_username)%%>', '<%%creatorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_by_username)%%>', urlencode('<%%creatorUsername%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_updated_by_username)%%>', '<%%editorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_updated_by_username)%%>', urlencode('<%%editorUsername%%>'), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$hasSelectedId) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('courses_table');
	if($hasSelectedId) {
		$jdata = get_joined_record('courses_table', $selectedId);
		if($jdata === false) $jdata = get_defaults('courses_table');
		$rdata = $row;
	}
	$templateCode .= loadView('courses_table-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: courses_table_dv
	if(function_exists('courses_table_dv')) {
		$args = [];
		courses_table_dv(($hasSelectedId ? $selectedId : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}