function faculty_table_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('faculty_table');
	if(!$arrPerm['insert']) {
		$error_message = $Translation['no insert permission'];
		return false;
	}

	$data = [
		'name' => Request::val('name', ''),
		'gender' => Request::val('gender', 'Male'),
		'dob' => Request::dateComponents('dob', ''),
		'email' => Request::val('email', ''),
		'phone' => Request::val('phone', ''),
		'address' => Request::val('address', ''),
		'hire_date' => Request::dateComponents('hire_date', '1'),
		'department' => Request::val('department', ''),
		'designation' => Request::val('designation', ''),
		'created_at' => parseCode('<%%creationDateTime%%>', true),
		'created_by_username' => parseCode('<%%creatorUsername%%>', true),
	];

	// record owner is current user
	$recordOwner = getLoggedMemberID();

	$recID = tableInsert('faculty_table', $data, $recordOwner, $error_message);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID')) && $recID !== false)
		faculty_table_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function faculty_table_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);
	$currentUsername = getLoggedMemberID();
	$errorMessage = '';

	// launch requests, asynchronously
	curl_batch($requests);
}

function faculty_table_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('faculty_table', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: faculty_table_before_delete
	if(function_exists('faculty_table_before_delete')) {
		$args = [];
		if(!faculty_table_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: ''
			);
	}

	sql("DELETE FROM `faculty_table` WHERE `id`='{$selected_id}'", $eo);

	// hook: faculty_table_after_delete
	if(function_exists('faculty_table_after_delete')) {
		$args = [];
		faculty_table_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='faculty_table' AND `pkValue`='{$selected_id}'", $eo);
}

function faculty_table_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('faculty_table', $selected_id, 'edit')) return false;

	$data = [
		'name' => Request::val('name', ''),
		'gender' => Request::val('gender', ''),
		'dob' => Request::dateComponents('dob', ''),
		'email' => Request::val('email', ''),
		'phone' => Request::val('phone', ''),
		'address' => Request::val('address', ''),
		'hire_date' => Request::dateComponents('hire_date', ''),
		'department' => Request::val('department', ''),
		'designation' => Request::val('designation', ''),
		'last_updated_at' => parseCode('<%%editingDateTime%%>', false),
		'last_updated_by_username' => parseCode('<%%editorUsername%%>', false),
	];

	// get existing values
	$old_data = getRecord('faculty_table', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: faculty_table_before_update
	if(function_exists('faculty_table_before_update')) {
		$args = ['old_data' => $old_data];
		if(!faculty_table_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'faculty_table',
		backtick_keys_once($set),
		['`id`' => $selected_id],
		$error_message
	)) {
		echo $error_message;
		echo '<a href="faculty_table_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	update_calc_fields('faculty_table', $data['selectedID'], calculated_fields()['faculty_table']);

	// hook: faculty_table_after_update
	if(function_exists('faculty_table_after_update')) {
		if($row = getRecord('faculty_table', $data['selectedID'])) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!faculty_table_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('faculty_table', $selected_id);
}

