		$templateCode = str_replace('<%%HTMLAREA(address)%%>', '<textarea maxlength="65500" name="address" id="address" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(hire_date)%%>', '1', $templateCode);
